import './Team.css';
import React, { useState } from 'react';
import MemberCard from '../MemberCard/MemberCard';

function Team({team_title, team_members}) {
  const [whichColor, setWhichColor] = useState(false);
  const [mainColor , setMainColor]  = useState();
  const [bgColor   , setBgColor]    = useState();

  const main_color_even  = "#57C278";
  const main_color_odden = "#FFb8f4";

  const bg_color_even  = "#D9F7E9";
  const bg_color_odden = "#E8F8FF";

  function changeColor(){
    if (team_members.length < 1){
      return false;
    }
    setWhichColor(!whichColor);

    setMainColor(whichColor ? main_color_even : main_color_odden);
    setBgColor(whichColor   ? bg_color_even   : bg_color_odden);

    return true;
  }

  const hasMemberOnTeam = changeColor();

  return (
    hasMemberOnTeam ? "" :
    <section className="team" style={{ backgroundColor:bgColor }} >
      <h3  style={{ borderColor:mainColor }}>
        {team_title}
      </h3>

      <div className='team_members'>
        {team_members.map(member =>
            <MemberCard key        = {member.name}
                        member     = {member}
                        main_color = {mainColor}
                        bg_color   = {bgColor}
            />
        )}
      </div>
    </section>
  );
}

export default Team;
