import React, { useState, useEffect } from 'react';


function Lattes() {
    const [data, setData] = useState('');

    const code = "K4999838P8"

    useEffect(() => {
        fetch('https://buscatextual.cnpq.br/buscatextual/preview.do?metodo=apresentar&id=' + code)
          .then(response => response.text())
          .then(data => setData(data));
      }, []);

    return (
		<section>
			<div>
                <label>CARREGANDO {code}:</label> <br />
                <pre>{data}</pre> <br />
                <label>FIM</label>
            </div>
		</section>
	);
}

export default Lattes;
