import './Team.css';
import React, { useState } from 'react';
import MemberCard from '../MemberCard/MemberCard';

function Team({team_title, team_members}) {
  const [whichColor, setWhichColor] = useState(false);

  const main_color_even  = "#57C278";
  const main_color_odden = "#FFb8f4";

  const bg_color_even  = "#D9F7E9";
  const bg_color_odden = "#E8F8FF";

  return (
    team_members.length < 1 ? "" :
    <section className="team" style={{ backgroundColor:main_color_even }} >
      <h3  style={{ borderColor:bg_color_even }}>
        {team_title}
      </h3>

      <div className='team_members'>
        {team_members.map((member, i) => {
          console.log("VALOR DE I ", i);
          const mcolor = i % 2 === 0 ? main_color_even : main_color_odden;
          const bcolor = i % 2 === 0 ? bg_color_even : bg_color_odden;
          return (
            <MemberCard key        = {member.name}
                        member     = {member}
                        main_color = {mcolor}
                        bg_color   = {bcolor}
            />
        )}
        )}
      </div>
    </section>
  );
}

export default Team;
