function loadLattesData(code){
    let url = "https://buscatextual.cnpq.br/buscatextual/preview.do?metodo=apresentar&id=" + code

    fetch(url)
    .then(function (response) {
        switch (response.status) {
            // status "OK"
            case 200:
                return response.text();
            // status "Not Found"
            case 404:
                throw response;
        }
    })
    .then(function (template) {
        return template
    })
    .catch(function (response) {
        // "Not Found"
        return response.statusText
    });

}

function Lattes() {
    const code = "K4999838P8"
	return (
		<section>
			<div>
                <label>CARREGANDO {code}:</label> <br />
                <label>{ loadLattesData(code) }</label>
            </div>
		</section>
	);
}

export default Lattes;
