import './MemberCard.css'
import React, { useState , useEffect } from 'react';

function MemberCard({member, main_color, bg_color}) {

    const [showPopup, setShowPopup] = useState(false);
    const [whichColor, setWhichColor] = useState(false);

    const main_color_even  = "#00b8f4";
    const main_color_odden = "#FFb8f4";

    const bg_color_even  = "#D9F7E9";
    const bg_color_odden = "#E8F8FF";

    const maincolor = whichColor ? main_color_even : main_color_odden;
    const bgcolor   = whichColor ? bg_color_even   : bg_color_odden;

    function show_resume(){
        setShowPopup(!showPopup);
    }

    //useEffect(() => {
    //    setWhichColor(prevState => !prevState);
    //}, []);

    function changeColor(){
        setWhichColor(!whichColor);
    }

	return (
		<div className="membercard" onLoad={changeColor}>
			<div className='header' style={{ backgroundColor:  maincolor}}>
                <img src={member.image} alt={member.name} />
            </div>

            <div className='footer' style={{ backgroundColor:bgcolor }}>
                <h4>{member.name}</h4>
                <h5><a href={member.lattes}>Lattes</a></h5>
                <div className="resume_popup" onClick={ show_resume }> +
                    <span className={showPopup ? 'resume_text show' : 'resume_text'} 
                          id={member.name}>{ member.resume }</span>
                </div>
            </div>
		</div>
	);
}

export default MemberCard;
